import React from 'react';
import { Form, Header, Segment, Button } from 'semantic-ui-react'

// does not do any validation
export default class NewResultsForm extends React.Component {
    state = {
        name: '',
        amount: '',
        yield: ''
    };
    onChangeCryptocurrency_pair = this._onChangeCryptocurrency_pair.bind(this);
    onChangeAmount = this._onChangeAmount.bind(this);
    onSubmit = this._onSubmit.bind(this);
    render() {
        return (
            <div className="ui container">
                <Segment vertical>
                    <Header>Please Enter Your Cryptocurrency Portfolio to Optimise</Header>
                    <Form onSubmit={this.onSubmit}>
                        <Form.Field>
                            <label>Cryptocurrency</label>
                            <input placeholder='E.g. ETH' value={this.state.name} onChange={this.onChangeCryptocurrency_pair} />
                        </Form.Field>
                        <Form.Field>
                            <label>Amount</label>
                            <input placeholder='999.99' value={this.state.amount} onChange={this.onChangeAmount} />
                        </Form.Field>
                        <Button type='submit'>Submit</Button>
                    </Form>
                </Segment>
            </div>
        );
    }
    _onChangeCryptocurrency_pair(e) {
        this.setState({
            name: e.target.value
        });
    }
    _onChangeAmount(e) {
        this.setState({
            amount: e.target.value
        });
    }
    _onSubmit() {
        const payload = {
            name: this.state.name,
            amount: parseFloat(this.state.amount),
            yield: 0.05
        };
        fetch('http://localhost:8080/results', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(payload)
        });
        this.setState({
            name: '',
            amount: '',
            yield:''
        });
    }
}
