import React from 'react';
import { Form, Header, Segment, Button } from 'semantic-ui-react'

// does not do any validation
export default class NewResultsForm extends React.Component {
    state = {
        pair: '',
        amount: '',
        pool_sz: ''
    };
    onChangeName = this._onChangeName.bind(this);
    onChangeTime = this._onChangeTime.bind(this);
    onChangePool_sz = this._onChangePool_sz.bind(this);
    onSubmit = this._onSubmit.bind(this);
    render() {
        return (
            <div className="ui container">
                <Segment vertical>
                    <div className="AppLogo">Hi</div>
                    <Header font-family="Consolas">Please Enter Your Cryptocurrency Portfolio and we will suggest how to maximize its yield:</Header>
                    <Form onSubmit={this.onSubmit}>
                        <Form.Field>
                            <label>Pair</label>
                            <input placeholder='ETH' value={this.state.pair} onChange={this.onChangeName} />
                        </Form.Field>
                        <Form.Field>
                            <label>Amount</label>
                            <input placeholder='Amount' value={this.state.amount} onChange={this.onChangeTime} />
                        </Form.Field>
                        <Form.Field>
                            <label>Pool Size</label>
                            <input placeholder='Pool Size' value={this.state.pool_sz} onChange={this.onChangePool_sz} />
                        </Form.Field>
                        <Button type='submit'>Submit</Button>
                    </Form>
                </Segment>
            </div>
        );
    }
    _onChangeName(e) {
        this.setState({
            pair: e.target.value
        });
    }
    _onChangeTime(e) {
        this.setState({
            amount: e.target.value
        });
    }
    _onChangePool_sz(e) {
        this.setState({
            pool_sz: e.target.value
        });
    }
    _onSubmit() {
        const payload = {
            pair: this.state.pair,
            amount: parseFloat(this.state.amount),
            pool_sz: parseFloat(this.state.pool_sz)
        };
        fetch('http://localhost:8080/results', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(payload)
        });
        this.setState({
            pair: '',
            amount: '',
            pool_sz: ''
        });
    }
}
