import React from 'react';
import { Form, Header, Segment, Button } from 'semantic-ui-react'

// does not do any validation
export default class NewResultsForm extends React.Component {
    state = {
        cryptocurrency_pair: '',
        amount: ''
    };
    onChangeName = this._onChangeName.bind(this);
    onChangeTime = this._onChangeTime.bind(this);
    onSubmit = this._onSubmit.bind(this);
    render() {
        return (
            <div className="ui container">
                <Segment vertical>
                    <Header>Please Enter Your Cryptocurrency Portfolio to Optimise</Header>
                    <Form onSubmit={this.onSubmit}>
                        <Form.Field>
                            <label>Cryptocurrency</label>
                            <input placeholder='E.g. ETH' value={this.state.name} onChange={this.onChangeName} />
                        </Form.Field>
                        <Form.Field>
                            <label>Amount</label>
                            <input placeholder='999.99' value={this.state.time} onChange={this.onChangeTime} />
                        </Form.Field>
                        <Button type='submit'>Submit</Button>
                    </Form>
                </Segment>
            </div>
        );
    }
    _onChangeName(e) {
        this.setState({
            cryptocurrency_pair: e.target.value
        });
    }
    _onChangeTime(e) {
        this.setState({
            time: e.target.value
        });
    }
    _onSubmit() {
        const payload = {
            name: this.state.cryptocurrency_pair,
            time: parseFloat(this.state.time)
        };
        fetch('http://localhost:8080/results', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(payload)
        });
        this.setState({
            cryptocurrency_pair: '',
            time: ''
        });
    }
}
